printf("a * b / c = %.3f\n", (float)a * b / c);  /* (float)a converte a para float: 1.0 */   /* 1.0 * b = 1.0 * 2 = 2.0 */   /* 2.0 / c = 2.0 / 3 ≈ 0.667 */   /* Saída: a * b / c = 0.667 */
printf("a * b %% c + 1 = %d\n", a * b % c + 1); /* a * b = 1 * 2 = 2 */ /* 2 % 3 = 2 */ /* 2 + 1 = 3 */ /* Saída: a * b % c + 1 = 3 */
printf("++a * b - c-- = %d\n", ++a * b - c--); /* ++a: a é incrementado antes da operação → a = 2 */ /* a * b = 2 * 2 = 4 */ /* c--: Usa c = 3, depois decrementa para c = 2 */ /* 4 - 3 = 1 */ /* Saída: ++a * b - c-- = 1 */ /* Novos valores: a = 2, c = 2 */
printf("7 - - b * ++d = %d\n", 7 - - b * ++d); /* ++d: Incrementa d antes da operação → d = 5 */ /* -b = -2 */ /* -2 * d = -2 * 5 = -10 */ /* 7 - (-10) = 7 + 10 = 17 */ /* Saída: 7 - - b * ++d = 17 */ /* Novo valor: d = 5 */
printf("a / b / c = %.3f\n", (float)a / b / c); /* (float)a = 2.0 */ /* 2.0 / b = 2.0 / 2 = 1.0 */ /*1.0 / c = 1.0 / 2 = 0.5 */ /* Saída: a / b / c = 0.500 */
printf("7 + c * --d / e = %.3f\n", 7 + c * --d / (float)e); /* --d: Decrementa d antes da operação → d = 4 */ /* c * d = 2 * 4 = 8 */ /* 8 / (float)e = 8 / 5.0 = 1.6 */ /* 7 + 1.6 = 8.6 */ /* Saída: 7 + c * --d / e = 8.600 */ /* Novo valor: d = 4 */
printf("2 * a %% - b + c + 1 = %d\n", 2 * a % - b + c + 1); /* 2 * a = 2 * 2 = 4 */ /* 4 % -b = 4 % -2 = 0 */ /* 0 + c + 1 = 0 + 2 + 1 = 3 */ /* Saída: 2 * a % - b + c + 1 = 3 */
printf("39 / - ++e - + 29 %% c = %.3f\n", 39.0 / - ++e - + 29 % c);  /* ++e: Incrementa e antes da operação → e = 6 */  /* 39.0 / -6 = -6.5 */  /* 29 % c = 29 % 2 = 1 */  /* -6.5 - 1 = -7.5 */  /* Saída: 39 / - ++e - + 29 %% c = -7.500 */  /* Novo valor: e = 6 */
printf("7 - + ++a %% (3+b) = %d\n", 7 - + ++a % (3+b));  /* ++a: Incrementa a antes da operação → a = 3 */  /* 3 + b = 3 + 2 = 5 */  /* 3 % 5 = 3 */  /* Saída: 7 - + ++a %% (3+b) = 4 */ /* 7 - 3 = 4 */ /* Novo valor: a = 3 */
